import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,e as c}from"./app.1ee10b43.js";const i="/Blog/assets/introduce.5e688655.png",s={},r=c('<h1 id="程序、进程、线程的理解" tabindex="-1"><a class="header-anchor" href="#程序、进程、线程的理解" aria-hidden="true">#</a> 程序、进程、线程的理解</h1><h2 id="程序-program" tabindex="-1"><a class="header-anchor" href="#程序-program" aria-hidden="true">#</a> 程序(program)</h2><div class="custom-container info"><p class="custom-container-title">概念</p><p>是为完成特定任务、用某种语言编写的一组指令的集合。即指一段静态的代码。</p></div><h2 id="进程-process" tabindex="-1"><a class="header-anchor" href="#进程-process" aria-hidden="true">#</a> 进程(process)</h2><div class="custom-container info"><p class="custom-container-title">概念</p><p>程序的一次执行过程，或是正在运行的一个程序。</p></div><div class="custom-container info"><p class="custom-container-title">说明</p><p>进程作为资源分配的单位，系统在运行时会为每个进程分配不同的内存区域</p></div><h2 id="线程-thread" tabindex="-1"><a class="header-anchor" href="#线程-thread" aria-hidden="true">#</a> 线程(thread)</h2><div class="custom-container info"><p class="custom-container-title">概念</p><p>进程可进一步细化为线程，是一个程序内部的一条执行路径。</p></div><div class="custom-container info"><p class="custom-container-title">说明</p><p>线程作为调度和执行的单位，每个线程拥独立的运行栈和程序计数器(pc)，线程切换的开销小。</p></div><h2 id="补充" tabindex="-1"><a class="header-anchor" href="#补充" aria-hidden="true">#</a> 补充</h2><div class="custom-container info"><p class="custom-container-title">内存结构</p><p><img src="'+i+'" alt="img.png" loading="lazy"></p><p>进程可以细化为多个线程。</p><p>每个线程，拥有自己独立的：栈、程序计数器</p><p>多个线程，共享同一个进程中的结构：方法区、堆。</p></div>',11),o=[r];function n(d,p){return e(),t("div",null,o)}const m=a(s,[["render",n],["__file","introduce.html.vue"]]);export{m as default};
