const e=JSON.parse('{"key":"v-c1ed0eaa","path":"/other/audition/JUC/%E4%B9%90%E8%A7%82%E9%94%81%E5%92%8C%E6%82%B2%E8%A7%82%E9%94%81.html","title":"乐观锁和悲观锁","lang":"zh-CN","frontmatter":{"date":"2023-04-03T00:00:00.000Z","category":"面试","tag":["JUC"]},"headers":[{"level":2,"title":"乐观锁","slug":"乐观锁","link":"#乐观锁","children":[{"level":3,"title":"概念","slug":"概念","link":"#概念","children":[]},{"level":3,"title":"乐观锁实现方式","slug":"乐观锁实现方式","link":"#乐观锁实现方式","children":[]}]},{"level":2,"title":"MybatisPlus使用乐观锁","slug":"mybatisplus使用乐观锁","link":"#mybatisplus使用乐观锁","children":[]},{"level":2,"title":"悲观锁","slug":"悲观锁","link":"#悲观锁","children":[]}],"git":{"createdTime":1680533037000,"updatedTime":1680533037000,"contributors":[{"name":"MonkeyBoss","email":"1912290362@qq.com","commits":1}]},"readingTime":{"minutes":1.08,"words":323},"filePathRelative":"other/audition/JUC/乐观锁和悲观锁.md","localizedDate":"2023年4月3日","excerpt":"<h1> 乐观锁和悲观锁</h1>\\n<h2> 乐观锁</h2>\\n<h3> 概念</h3>\\n<p>乐观锁：顾名思义，就是十分乐观，它总是认为不会出现问题，无论干什么都不去上锁~，如果出现了问题，再次更新值测试，这里使用了version字段。</p>\\n<p>也就是每次更新的时候同时维护一个version字段</p>\\n<h3> 乐观锁实现方式</h3>\\n<ul>\\n<li>取出记录时，获取当前的version</li>\\n<li>更新时，带上这个version</li>\\n<li>执行更新时，set version = newVersion where version = oldVersion</li>\\n<li>如果version不对，就更新失败</li>\\n</ul>"}');export{e as data};
